export var getHorizontalAlignment = function getHorizontalAlignment(str) {
  var align = {
    left: 'flex-start',
    right: 'flex-end',
    center: 'center',
    centerSpaceAround: 'space-around',
    spaceAround: 'space-around', // dupe
    centerSpaceBetween: 'space-between',
    spaceBetween: 'space-between', // dupe
    spaceEvenly: 'space-evenly',
    stretch: 'stretch',
    default: 'flex-start'
  };
  return align[str] || align['default'];
};

export var getVerticalAlignment = function getVerticalAlignment(str) {
  var align = {
    bottom: 'flex-end',
    top: 'flex-start',
    center: 'center',
    default: 'center'
  };
  return align[str] || align['default'];
};
